package asssignment1.day1;

public class OddNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=20;
		System.out.println("printing only the odd number from 1to 20 ");
		for(int i=1; i<=a;i++)
		{
			if(i%2!=0)
			{
				System.out.println(i+" ");
			}
		}

	}

}
